        $('.custom-month').html(html);
        $('.custom-year').html(year);



        function checkkill() {
            var kynang_check = [];
            $(".checkkill").each(function() {
                var $this = $(this);
                if ($this.prop('checked')) {
                    check = $this.val();
                    kynang_check.push(check);
                }
            });

            $("#kynang_chuyenmon").val(kynang_check);
        }
    </script>

